<?phpclass Weibo {    private $_accessToken;    public function __construct($token) {        $this->_accessToken = $token;     }    public function get($method,$params = array()) {        $strParams = implode(&,$params);        $token = $this->_accessToken;        $urlTpl = "http://api.weibo.com/2/%s?access_token=%s&$%s";         $url = sprintf($urlTpl,$method,$token,$strParams);        return $this->_httpGet($url);    }    private function _httpGet($url) {        $fetchUrl = new SaeFetchurl();         $content = $fetchUrl->fetch($url);        return $content;    }} 